package service;import model.Item;import org.springframework.beans.factory.annotation.Autowired;import repositories.ItemRepository;import java.util.List;public class ItemService{    @Autowired    ItemRepository itemRepository ;    public Item saveItem(Item p) {        return itemRepository.save(p);    }    public Item updatItem(Item p) { return itemRepository.save(p);}    public void deleteItem(Item p) {itemRepository.delete(p);}    public void deleteItemById(Long id) {itemRepository.deleteById(id);}    public Item getItem(Long id) {        return itemRepository.findById(id).get();    }    public List<Item> getAllItems() {        return itemRepository.findAll();    }}